import { Router } from "express";
import questionOneController from "../controllers/1-questionOne";

const { inputValidation } = questionOneController;

const router = new Router();

router.post("/input-validate", inputValidation);

export default router;

export default class questionOneController {
  static inputValidation(request, response) {
    const { data, rules } = request.body;
    const dataObjectProvided = Object.keys(data);
    const DataMissing = rules.filter((rule) =>
      dataObjectProvided.every((elem) => elem !== rule)
    );
    if (DataMissing.length === 0) {
      return response.status(200).json({ status: 200, message: "Valid" });
    }
    return response
      .status(400)
      .json({ status: 400, missingInputItems: DataMissing });
  }
}

import { Router } from "express";
import questionTwoController from "../controllers/2-questionTwo";

const { removeItem } = questionTwoController;

const router = new Router();

router.patch("/remove-item", removeItem);

export default router;

export default class questionTwoController {
  static removeItem(request, response) {
    const { data, item } = request.body;
    const dataObjectProvided = Object.keys(data).length;
    delete data[item];
    return Object.keys(data).length === dataObjectProvided
      ? response
          .status(404)
          .json({ status: 404, message: "attribute not found" })
      : response.status(200).json({ status: 200, output: data });
  }
}

import { Router } from "express";
import questionTwoController from "../controllers/3-questionThree";

const { lowestIndex } = questionTwoController;

const router = new Router();

router.post("/lowest-index", lowestIndex);

export default router;

export default class questionThreeController {
  static lowestIndex(request, response) {
    const { magic, distance } = request.body;
    if (
      toString.call(magic) !== "[object Array]" ||
      toString.call(distance) !== "[object Array]"
    ) {
      response.send("This function takes in only arrays");
    }
    // eslint-disable-next-line no-restricted-globals
    // if (!magic.some(isNaN) || !distance.some(isNaN)) {
    //   response.send("Correct! All Numbers");
    // } else {
    //   response.send("Only numbers are allowed in the array");
    // }
    let lowestIndex = magic[0];
    const both = magic.reduce((acc, elem, i) => {
      const figure = acc + elem - distance[i];
      if (figure < lowestIndex) lowestIndex = i;
      return figure;
    }, 0);
    return both < 0
      ? response.status(400).json({ status: 400, message: -1 })
      : response.status(200).json({ status: 200, message: lowestIndex });
  }
}

import chai, { expect } from "chai";
import chaiHttp from "chai-http";
import app from "../../server";
import { data, rules } from "./question-data";

chai.should();

chai.use(chaiHttp);
const url = "/api/v1";

describe("Testing Question One Endpoint", () => {
  describe("All Input Validation passes", () => {
    it("should return valid if all input items are present in the request body'", (done) => {
      chai
        .request(app)
        .post(`${url}/input-validate`)
        .send({
          data,
          rules,
        })
        .end((error, response) => {
          expect(response).to.have.status(200);
          expect(response.body.message).to.equal("Valid");
          done();
        });
    });
  });
});

